// Start with a try...catch block to handle any potential errors gracefully.
try {
  // Get the incoming data from the IF node.
  const inputData = $input.item;

  // ERROR CHECK 1: Ensure the .json property exists.
  if (!inputData.json) {
    throw new Error("The input from the If node did not contain the expected '.json' property.");
  }

  const agentData = inputData.json;

  // Get the combined string from the AI's output.
  const combinedString = agentData.block || agentData.text || agentData.output;

  // ERROR CHECK 2: Check if we found a string at all.
  if (!combinedString) {
    throw new Error("AI Agent output (from If node) did not contain a 'block', 'text', or 'output' field.");
  }

  // --- THE CRITICAL FIX ---
  // Split the string into two parts using our unique '|||' separator.
  const parts = combinedString.split('|||');

  // ERROR CHECK 3: Ensure we have both a message and a JSON part.
  if (parts.length < 2) {
    throw new Error("AI output was not in the expected 'message|||json' format.");
  }

  // The JSON data is the second part.
  const jsonString = parts[1];

  // The key step: Parse the extracted JSON string.
  const parsedData = JSON.parse(jsonString);

  // Return the successfully parsed object.
  return parsedData;

} catch (error) {
  // ERROR CHECK 4: Catch any other errors.
  console.error("Failed to parse JSON. The raw input was:", $input.item);
  throw new Error(`Error in Code Node: ${error.message}`);
}
